        ) VALUES (
            0,
            0   -- 2 PUNTOS POR AO (SE DEBE CALCULAR)
        ) RETURNING DCCA_IDDOCENCIA INTO ID_DOCENCIA;
                
        -- COMPETENCIA PROFESIONAL
