        ) VALUES (
            0,
            0   -- 1 PUNTO POR AO O PROPORCIONAL A FRACCION POR AO
        ) RETURNING PNAL_IDPROFESIONAL INTO ID_PROFESIONAL; 
        
        -- PRODUCCION ACADEMICA
            PRCN_PUNTAJE_TOTAL,
            PRCN_PUNTAJE_MAX
        ) VALUES (
            0,
            2
        ) RETURNING PRCN_IDPRODUCCION INTO ID_PRODUCCION;        
        
        -- CONOCIMIENTOS (ASPECTOS DE SELECCION)
            CNTO_DOMINIO_CONCEP,
            CNTO_PROFUNDIDAD,
            CNTO_ARGUMENTACION,
            CNTO_SEC_LOGICA,
            CNTO_ESTRA_DIDACTICA,
            CMDT_COMUNICACION,
            CNTO_TOTAL,
            CNTO_TOTAL_MAX
        ) VALUES (
            0,
            0,
            0,
            0,
            0,
            0,
            0,
            100
        ) RETURNING CNTO_IDCONOC_DETALLE INTO ID_CONOCIMIENTO;
        
        -- COMPETENCIA - TABLA GENERAL
            CMPC_PUNTAJE_TOTAL,
            CMPC_MAX_PUNTOS,
            PRSL_IDPERSONAL,
            INVN_IDINVESTIGACION,
